import React, {useState} from "react";
import {Calendar} from "primereact/calendar";
import { Dropdown } from 'primereact/dropdown';
import {useDispatch, useSelector} from "react-redux";
import {addNewBooking, hoursSelectItems} from "../ducks/bookings";
import {getUsername} from "../ducks/session";
import {Button} from "primereact/button";
import {push} from "connected-react-router";


function Booking() {
    const hourSelectItems = useSelector(hoursSelectItems);
    const [booking, setBooking] = useState({date:undefined, hour: undefined});
    const username = useSelector(getUsername);
    const dispatch = useDispatch();


    function handleBooking() {
        dispatch(addNewBooking({...booking, username}));
        dispatch(push("/"));
    }

    return (
        <div className="form">
            <h3>Boka tid</h3>
            <Calendar inline value={booking} onChange={(e) => setBooking({...booking, date: e.target.value})}/>
            <Dropdown value={booking} options={hourSelectItems} onChange={(e) => setBooking({...booking, hour: e.target.value})} placeholder="Select a City"/>
                <Button onClick={handleBooking} label="Boka" disabled={booking.date === undefined && booking.hour === undefined}/>
        </div>
    );
}
export default Booking;
