export const name = "bookings";

export const rolesState = {
    bookings: {
        date: undefined,
        time: undefined,
    },
};

export function reducer(state = bookingsState, action) {
    switch (action.type) {
        case "ADD_BOOKING":
            return {
                ...state,
                roles: [...state.roles, action.payload],
            };
        case "DELETE_BOOKING":
            return {
                ...state,
                roles: state.roles.filter((role) => role.name !== action.payload),
            };
        default:
            return state;
    }
}

// selector
export function hoursSelectItems(state) {
    return state[name].bookings;
}

// actions
export function addNewBooking(role) {
    return {
        type: "ADD_BOOKING",
        payload: role,
    };
}

export function deleteBooking(name) {
    return {
        type: "DELETE_BOOKING",
        payload: name,
    };
}
